mapper.select:
select

  distinct


from u_order




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select u.type type, u.order_id oId,u.rid rId,u.cid cId,re.`name` rName,re.telephone rTelephone,co.`name` cName,u.order_time orderTime,
u.receving_time recevingTime,u.end_time endTime,u.reserve closeTime,co.color cColor,co.price cPrice,co.commodityImg img,co.isNoPackagemaill video

		from commodity co, receiving_note re, u_order u,
				(select su.sid ,su.`name` 
				from supplier su ,user us 
				where su.sid = us.sid and us.account = #{account} ) a 

		where u.sid = a.sid and re.id = u.rid and co.cid = u.cid  and u.type != '4'

		and u.type = #{type}



   order by ${orderByClause}



     limit ${offset}, ${limit}


     limit ${limit}

select count(*)
		from commodity co, receiving_note re, u_order u,
				(select su.sid ,su.`name` 
				from supplier su ,user us 
				where su.sid = us.sid and us.account = #{account} ) a 
		where u.sid = a.sid and re.id = u.rid and co.cid = u.cid and u.type = #{type}

 SELECT d.`name` cName,d.commodityImg cImg,d.cid cId,e.account account,e.`name` uName,e.headimg uImg,c.order_id oId
from(select a.order_id ,TIMESTAMPDIFF(SECOND,NOW(),timestampadd(day, 3, a.end_time) ) time from u_order a where a.type='3'
)b,u_order c,commodity d,`user` e where b.time <0 AND b.order_id = c.order_id AND d.cid=c.cid AND c.account = e.account
